#define DEFINE_GLOBAL
#include "OpenGL45.h"

void *GetOpenGLFunction(LPCSTR name)
{
	void *ptr = wglGetProcAddress(name);

	if (ptr == nullptr)
	{
		HMODULE module = LoadLibraryA("opengl32.dll");
		ptr = GetProcAddress(module, name);
	}

	return ptr;
}

